mapper.select:
select rownum, b.*
from(select rownum as rm, a.*
from(select * from kh_user 
where #{searchKey} like '%' || #{searchWord} || '%'
)a)b
where b.rm>=#{startRow} and b.rm <=#{endRow}

select rownum, b.*
from(select rownum as rm, a.*
from(select * from book 
where #{searchKey} like '%' || #{searchWord} || '%'
)a)b
where b.rm>=#{startRow} and b.rm <=#{endRow}

mapper.update:
update kh_user
set upass=#{upass}
, uname=#{uname}
, uemail=#{uemail}
, uphone=#{uphone}
, ubirth=#{ubirth}
, ugender=#{ugender}
where id=#{id}

update book 
set bname=#{bname}
, gno=#{gno}
, bthumb=#{bthumb}
, binfo=#{binfo}
where bno=#{bno}

mapper.select:
select rownum, b.*
from(select rownum as rm, a.*
from(select * from author 
where #{searchKey} like '%' || #{searchWord} || '%'
)a)b
where b.rm>=#{startRow} and b.rm <=#{endRow}
