mapper.select:
select rm
from (
	select rownum as rm, a.* 
	from (
		select *
		from serial 
		where bno=#{bno}
		order by upno
		)a
	)b
where b.upno=#{upno}

select b.upno
from (
	select rownum as rm, a.* 
	from (
		select *
		from serial 
		where bno=#{bno}
		order by upno
		)a
	)b
where rm=#{rm}
