mapper.select:
select rownum, a.*
from (select rownum as rm, serial.* from serial where bno=#{bno} order by upno)a
where a.rm=#{rm}

select rm
from (
	select rownum as rm, a.* 
	from (
		select *
		from serial 
		where bno=#{bno}
		order by upno
		)a
	)b
where upno=#{upno}

mapper.update:
update grade
set grade=#{grade}
where id=#{id}
and upno=#{upno}
